// 通过这个函数，你可以验证你实现的SIMD哈希函数的正确性
int main()
{
    
    vector<string> passwords = {"", 
        "", 
        "", 
        ""};
        bit32 state[16] = {0}; // 4个密码 * 4个状态值
        SIMD_MD5Hash(passwords, state);
        
        // 打印结果
        for (int i = 0; i < 4; i++) {
            cout << "Password " << i+1 << " MD5: ";
            for (int j = 0; j < 4; j++) {
                cout << setw(8) << setfill('0') << hex << state[i*4 + j];
            }
            cout << endl;
        }
        
        // 验证单个密码的结果
        bit32 single_state[4];
        MD5Hash(passwords[0], single_state);
        cout << "Single MD5: ";
        for (int i = 0; i < 4; i++) {
            cout << setw(8) << setfill('0') << hex << single_state[i];
        }
        cout << endl;
}